import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input, Checkbox } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import { Link, Redirect } from 'react-router-dom';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';
import {
  FormItem,
  FormItemError,
  Form,
  FormCheckboxWrap,
  FormCheckboxTitle,
  FormSignInLink,
  FormTitle,
  FormSubmitButtonStyles,
} from '../../styled-components';

import { signInUrl } from '../../routes';

const formikInicialValues = {
  login: '',
  email: '',
  password: '',
  repeatPassword: '',
  acceptTerms: false,
};

const formItems = [
  {
    id: uniqueId(), title: 'Username', name: 'login', placeholder: 'Username',
  },
  {
    id: uniqueId(), title: 'Email address', name: 'email', placeholder: 'Email',
  },
  {
    id: uniqueId(), title: 'Password', name: 'password', placeholder: 'Password',
  },
  {
    id: uniqueId(), title: 'Repeat Password', name: 'repeatPassword', placeholder: 'Repeat password',
  },
];

const formikValidationSchema = Yup.object({
  login: Yup.string()
    .max(30, 'Your username should be no more than 30 characters!')
    .required('required'),
  email: Yup.string()
    .email('Invalid email')
    .required('required'),
  password: Yup.string()
    .min(8, 'Your password need to be at least 6 characters.')
    .max(50, 'Your password should be no more than 50 characters.')
    .matches(/^[A-Za-z0-9]+$/, 'Password can be only with latin symbols and numbers')
    .matches(/(?=.*[0-9])/, 'Your password need to be at least 1 number')
    .matches(/(?=.*[A-Z])/, 'Your password need to be at least 1 uppercase symbol')
    .required('required'),
  repeatPassword: Yup.string()
    .oneOf([Yup.ref('password'), null], 'Passwords must match')
    .required('required'),
  acceptTerms: Yup.bool()
    .oneOf([true])
    .required('You should accept terms'),
});

const mapStateToProps = (state) => {
  const { signUpState } = state;
  const isInputsDisable = (signUpState === 'requested');
  const isSignUpRequestSuccess = (signUpState === 'finished');

  return { isInputsDisable, signUpState, isSignUpRequestSuccess };
};

const actionCreators = {
  signUp: actions.signUp,
};

const SignUp = (props) => {
  const { isInputsDisable, signUp, isSignUpRequestSuccess } = props;

  const handleOnSignUp = async (formik) => {
    const { login, email, password } = formik.values;
    const newUser = {
      user: {
        username: login,
        email,
        password,
      },
    };
    signUp(newUser);
    formik.resetForm();
  };

  const ifAuthRedirectToSignIn = () => (
    (isSignUpRequestSuccess) ? (<Redirect to={signInUrl} />) : null
  );

  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      // eslint-disable-next-line no-use-before-define
      handleOnSignUp(formik);
    },
  });

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={(formItem.name === 'password' || formItem.name === 'repeatPassword') ? 'password' : 'text'}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
          disabled={isInputsDisable}
        />
        {formik.touched[formItem.name] && formik.errors[formItem.name] ? (
          <FormItemError>{formik.errors[formItem.name]}</FormItemError>
        ) : null}
      </FormItem>
    ))
  );

  return (
    <Form onSubmit={formik.handleSubmit}>
      {ifAuthRedirectToSignIn()}
      <FormTitle>Create new account</FormTitle>

      {renderInputs()}

      <FormItem>
        <FormCheckboxWrap>
          <Checkbox
            id="acceptTerms"
            name="acceptTerms"
            checked={formik.values.acceptTerms}
            onChange={formik.handleChange}
            disabled={isInputsDisable}
          />
          <FormCheckboxTitle
            htmlFor="acceptTerms">
            I agree to the processing of my personal information
          </FormCheckboxTitle>
        </FormCheckboxWrap>
        {formik.touched.acceptTerms && formik.errors.acceptTerms ? (
          <FormItemError>{formik.errors.acceptTerms}</FormItemError>
        ) : null}
      </FormItem>

      <FormItem>
        <Button
          htmlType="submit"
          type="primary"
          onClick={formik.handleSubmit}
          disabled={isInputsDisable}
          style={FormSubmitButtonStyles}
        >
          Create
        </Button>
      </FormItem>

      <FormSignInLink>
        <span>Already have account?</span>
        <Link to={signInUrl}> Sign In.</Link>
      </FormSignInLink>
    </Form>
  );
};

SignUp.propTypes = {
  signUp: propTypes.func,
  isInputsDisable: propTypes.bool,
  isSignUpRequestSuccess: propTypes.bool,
};

SignUp.defaultProps = {
  signUp: null,
  isInputsDisable: false,
  isSignUpRequestSuccess: false,
};

export default connect(mapStateToProps, actionCreators)(SignUp);
