import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { getDate } from '../../helper';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats,
  ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfoWrap, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';

const Article = (props) => props.articles.map((article) => {
  const {
    author, createdAt, description, favoritesCount, favorited, id, tagList, title, slug,
  } = article;
  const { username, image } = author;
  const dateDiff = getDate(createdAt);
  const [isLiked, setIsLiked] = useState(favorited);

  const renderTagList = () => (
    tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
  );

  const handleOnLikeArticle = () => {
    setIsLiked(!isLiked);
  }

  return (
    <ArticlesItem key={id}>
      <ArticlesInfo>
        <ArticlesHeader>
          <Link style={articlesTitle} to={articleUrl(slug)}>
            {title}
          </Link>
          <ArticlesStats>
            <ArtickesLikeImg
              onClick={handleOnLikeArticle}
              src={favorited ? activeLikeSrc : disabledLikeSrc}
            />
            <ArticlesLikesCount>{favoritesCount}</ArticlesLikesCount>
          </ArticlesStats>
        </ArticlesHeader>
        <ArticlesTagsWrap>
          {renderTagList()}
        </ArticlesTagsWrap>

        <ArticlesDescription>
          {description}
        </ArticlesDescription>
      </ArticlesInfo>

      <ArticlesUserInfoWrap>
        <ArticlesUserInfoGroup>
          <ArticlesUsername>{username}</ArticlesUsername>
          <ArticlesDate>{dateDiff}</ArticlesDate>
        </ArticlesUserInfoGroup>
        <ArticlesUserImage src={image} />
      </ArticlesUserInfoWrap>
    </ArticlesItem>
  );
});

export default Article;
