import React from 'react';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import { Link } from 'react-router-dom';
import propTypes from 'prop-types';
import { EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import 'antd/dist/antd.css';
import {
  FormItem,
  Form,
  FormTitle,
  FormSubmitButtonStyles,
  FormSignUpLink,
  FormSignInWrapper, FormItemError,
} from '../../styled-components';
import * as actions from '../../redux/actions';
import { signUpUrl } from '../../routes';
import { renderErrorMessage } from '../../helper';

const formikInitialValues = {
  email: '',
  password: '',
};

const mapStateToProps = (state) => {
  const { signInState } = state;
  const isInputsDisable = (signInState === 'requested');

  return { isInputsDisable };
};

const actionCreators = {
  signIn: actions.signIn,
};

const SignIn = (props) => {
  const { isInputsDisable, signIn } = props;

  const handleOnAuthorizationUser = (formik) => {
    const { email, password } = formik.values;
    const userData = {
      user: {
        email,
        password,
      },
    };
    signIn(userData, formik);
  };

  const formik = useFormik({
    initialValues: formikInitialValues,
    onSubmit: () => {
      handleOnAuthorizationUser(formik);
    },
  });

  return (
    <FormSignInWrapper>
      <Form onSubmit={formik.handleSubmit}>
        <FormTitle>Sign In</FormTitle>
        <FormItem htmlFor="email">
          Email address:
          <Input
            placeholder="Email address"
            id="email"
            name="email"
            onChange={formik.handleChange}
            value={formik.values.email}
            disabled={isInputsDisable}
          />
          {renderErrorMessage(formik, 'email')}
        </FormItem>

        <FormItem htmlFor="password">
          Password:
          <Input.Password
            id="password"
            name="password"
            placeholder="Password"
            onChange={formik.handleChange}
            value={formik.values.password}
            iconRender={(visible) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
            disabled={isInputsDisable}
          />
          {renderErrorMessage(formik, 'password')}
        </FormItem>

        <FormItem>
          <Button
            type="primary"
            htmlType="submit"
            className="form-submit"
            onClick={formik.handleSubmit}
            style={FormSubmitButtonStyles}
            disabled={isInputsDisable}
          >
            Войти
          </Button>
        </FormItem>
        <FormSignUpLink>
          <span>Don't have account?</span>
          <Link to={signUpUrl}> Sign Up.</Link>
        </FormSignUpLink>
      </Form>
    </FormSignInWrapper>

  );
};

SignIn.propTypes = {
  signIn: propTypes.func,
  isInputsDisable: propTypes.bool,
};

SignIn.defaultProps = {
  signIn: null,
  isInputsDisable: false,
};

export default connect(mapStateToProps, actionCreators)(SignIn);
