import React, { useEffect, useState } from 'react';
import Loader from 'react-loader-spinner'
import { uniqueId } from 'lodash';
import { Pagination } from 'antd';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../redux/actions';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';
import userDefaultImgSrc from '../../images/user-default-img.svg';
import {
  ArticlesWrapper,
  PaginationWrapper,
  ArticlesDate,
  ArticlesUserImage,
  ArticlesUsername,
  ArticlesUserInfoGroup,
  ArticlesUserInfo,
  ArticlesDescription,
  ArticlesTag,
  ArticlesStats,
  ArticlesTagsWrap,
  ArticlesLikesCount,
  articlesTitle,
  ArtickesLikeImg,
  ArticlesItemsWrapper,
  ArticlesItem,
  ArticlesInfo,
  ArticlesHeader,
  LoaderWrapper,
} from '../../styled-components';
import { articleUrl } from '../../routes';

const mapStateToProps = (state) => {
  const { articles, getArticlesState } = state;
  console.log(state);
  return {
    articles,
    isArticlesReady: getArticlesState === 'finished',
    isRequestProcessed: getArticlesState === 'processed',
  };
}

const actionCreator = {
  getArticles: actions.getArticles,
}

const ArticlesList = (props) => {
  const { getArticles, articles, isArticlesReady, isRequestProcessed } = props;

  const getArticlesRequest = async () => {
    await getArticles();
  }

  const renderLoader = () => (
    <LoaderWrapper>
      <Loader type="Oval" color="rgb(82, 196, 26)" height={200} width={200} />
    </LoaderWrapper>
  );

  const renderPagination = () => (
    <Pagination
      size="small"
      total={500}
      pageSize={5}
      showSizeChanger={false}
      onChange={handleOnChangePagination}
    />
  );

  useEffect(() => {
    if (!isArticlesReady) getArticlesRequest();
  }, []);

  const renderArticles = (pageNumber = 1, pageSize = 5) => {

    const from = pageNumber * pageSize - pageSize;
    const to = pageNumber * pageSize
    const currentArticlesArray = Array.from(articles).slice(from, to);

    return currentArticlesArray.map((article) => {
      const { author, createdAt, description, favoritesCount, favorited, id, tagList, title } = article;
      const { username, image } = author;
      const initialTagList = tagList.map((tagName) => ({ id: uniqueId(), title: tagName }));

      const renderTagList = () => (
        initialTagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
      );

      return (
        <ArticlesItem key={id}>
          <ArticlesInfo>
            <ArticlesHeader>
              <Link style={articlesTitle} to={articleUrl}>
                {title}
              </Link>
              <ArticlesStats>
                <ArtickesLikeImg src={favorited ? activeLikeSrc : disabledLikeSrc} />
                <ArticlesLikesCount>{favoritesCount}</ArticlesLikesCount>
              </ArticlesStats>
            </ArticlesHeader>
            <ArticlesTagsWrap>
              {renderTagList()}
            </ArticlesTagsWrap>

            <ArticlesDescription>
              {description}
            </ArticlesDescription>
          </ArticlesInfo>

          <ArticlesUserInfo>
            <ArticlesUserInfoGroup>
              <ArticlesUsername>{username}</ArticlesUsername>
              <ArticlesDate>March 5, 2020</ArticlesDate>
            </ArticlesUserInfoGroup>
            <ArticlesUserImage src={image} />
          </ArticlesUserInfo>
        </ArticlesItem>
      );
    })
  };

  const handleOnChangePagination = (value, pageSize) => {
    renderArticles(value, pageSize);
  }

  return (
    <ArticlesWrapper>
      <ArticlesItemsWrapper>
        {(isRequestProcessed || isArticlesReady) ? renderArticles() : renderLoader() }
      </ArticlesItemsWrapper>

      <PaginationWrapper>
        {renderPagination()}
      </PaginationWrapper>
    </ArticlesWrapper>
  )
}

export default connect(mapStateToProps, actionCreator)(ArticlesList);
