import React from 'react';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import { Link } from 'react-router-dom';
import propTypes from 'prop-types';
import { EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import 'antd/dist/antd.css';
import styled from 'styled-components';
import * as actions from '../../redux/actions';

const SignInWrapper = styled.div`
  display: flex;
  flex-direction: column;
  padding: 48px 32px;
  background: #FFFFFF;
  border: 1px solid #D9D9D9;
  box-sizing: border-box;
  box-shadow: 0px 0.608796px 2.93329px rgba(0, 0, 0, 0.0196802),
              0px 1.46302px 7.04911px rgba(0, 0, 0, 0.0282725), 
              0px 2.75474px 13.2728px rgba(0, 0, 0, 0.035), 
              0px 4.91399px 23.6765px rgba(0, 0, 0, 0.0417275), 
              0px 9.19107px 44.2843px rgba(0, 0, 0, 0.0503198), 
              0px 22px 106px rgba(0, 0, 0, 0.07);
  border-radius: 6px;
`;

const formikInitialValues = {
  email: '',
  password: '',
};

const mapStateToProps = (state) => {
  const { signInState } = state;
  const isInputsDisable = (signInState === 'requested');

  return { isInputsDisable };
};

const actionCreators = {
  signIn: actions.signIn,
};

const SignIn = (props) => {
  const { isInputsDisable, signIn } = props;

  const handleOnAuthorizationUser = () => {
    // eslint-disable-next-line no-use-before-define
    const { email, password } = formik.values;
    const userData = {
      user: {
        email,
        password,
      },
    };
    signIn(userData);
  };

  const formik = useFormik({
    initialValues: formikInitialValues,
    onSubmit: () => {
      handleOnAuthorizationUser();
    },
  });

  return (
    <SignInWrapper>
      <form onSubmit={formik.handleSubmit} className="form">
        <div className="form-title">Авторизация</div>
        <label htmlFor="email" className="form-item">
          Почта:
          <Input
            placeholder="Введите Email"
            id="email"
            name="email"
            onChange={formik.handleChange}
            value={formik.values.email}
            disabled={isInputsDisable}
          />
        </label>

        <label htmlFor="password" className="form-item">
          Пароль:
          <Input.Password
            id="password"
            name="password"
            placeholder="Введите Пароль"
            onChange={formik.handleChange}
            value={formik.values.password}
            iconRender={(visible) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
            disabled={isInputsDisable}
          />
        </label>

        <div className="form-item">
          <Button
            type="primary"
            htmlType="submit"
            className="form-submit"
            onClick={formik.handleSubmit}
          >
            Войти
          </Button>
          <Link to="sign-up" className="form-link">Регистрация</Link>
        </div>
      </form>
    </SignInWrapper>

  );
};

SignIn.propTypes = {
  signIn: propTypes.func,
  isInputsDisable: propTypes.bool,
};

SignIn.defaultProps = {
  signIn: null,
  isInputsDisable: false,
};

export default connect(mapStateToProps, actionCreators)(SignIn);
