import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import { signInUrl, signUpUrl, mainUrl, createArticleUrl } from '../../routes';
import propTypes from 'prop-types';
import { FormTitle, ArticlesUserImage } from '../../styled-components';
import userDefaultImgUrl from '../../images/user-default-img.svg';

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const HeaderIsAuth = styled.div`
  display: flex;
  flex-direction: row;
`;

const HeaderIsNotAuth = styled.div`

`;

const headerSignInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
};

const headerSignUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
};

const headerCreateArticleStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
}

const mapStateToProps = (state) => {
  const { isAuthorized, username, userProfileImage } = state.userData;

  console.log((userProfileImage === null || undefined));
  return {
    isAuthorized,
    username,
    userProfileImage: (userProfileImage === null || undefined) ? userDefaultImgUrl : userProfileImage,
  };
}

const actionCreator = {

}

const Header = (props) => {
  const { isAuthorized, username, userProfileImage } = props;
  console.log(userProfileImage);
  const renderHeaderMenu = () => {
    if (isAuthorized) {
      return (
        <HeaderIsAuth>
          <NavLink to={createArticleUrl} style={headerCreateArticleStyles}>Create article</NavLink>
          <FormTitle>{username}</FormTitle>
          <ArticlesUserImage src={userProfileImage} />
          <NavLink to={signUpUrl} style={headerSignUpStyles}>Sign Up</NavLink>
        </HeaderIsAuth>
      );
    }

    // если пользователь неавторизирован
    return (
      <HeaderIsNotAuth>
        <NavLink to={signInUrl} style={headerSignInStyles}>Sign In</NavLink>
        <NavLink to={signUpUrl} style={headerSignUpStyles}>Sign Up</NavLink>
      </HeaderIsNotAuth>
    )
  }

  return (
    <HeaderWrapper>
      <NavLink to={mainUrl} style={headerSignInStyles}>Realworld Blog</NavLink>
      {renderHeaderMenu()}
    </HeaderWrapper>
  )
}

Header.propTypes = {
  isAuthorized: propTypes.bool,
  username: propTypes.string,
  userProfileImage: propTypes.string,
}

Header.defaultProps = {
  isAuthorized: false,
  username: null,
  userProfileImage: null,
}

export default connect(mapStateToProps, actionCreator)(Header);
