import React from 'react';
import { Link } from 'react-router-dom';
import { getDate } from '../../helper';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats,
  ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfoWrap, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';

const Article = (props) => {
  const { articles, fakeLikes, setFakeLikes } = props;

  return articles.map((article) => {
    const {
      author, createdAt, description, favorited, id, tagList, title, slug,
    } = article;
    const { username, image } = author;
    const dateDiff = getDate(createdAt);

    const renderTagList = () => (
      tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
    );

    // визуальное изменение лайка для пользователя
    const getFakeLike = () => {
      if (fakeLikes === undefined) return null;
      return fakeLikes.filter((el) => el.id === id)[0];
    };

    // рендер лайка
    const fakeLikeSrc = () => {
      if (getFakeLike() === undefined) return disabledLikeSrc;
      return getFakeLike().isLiked ? activeLikeSrc : disabledLikeSrc;
    };

    // рендер счетчика лайков
    const fakeLikeCounter = () => {
      if (getFakeLike() === undefined) return 0;
      return getFakeLike().likesCount;
    };

    const handleOnLikeArticle = () => {
      setFakeLikes((state) => {
        const newState = state.map((el) => {
          if (el.id === id) {
            return {
              ...el,
              isLiked: !el.isLiked,
              likesCount: (el.isLiked) ? el.likesCount - 1 : el.likesCount + 1,
            };
          }
          return el;
        });
        return newState;
      });
    };

    return (
      <ArticlesItem key={id}>
        <ArticlesInfo>
          <ArticlesHeader>
            <Link style={articlesTitle} to={articleUrl(slug)}>
              {title}
            </Link>
            <ArticlesStats>
              <ArtickesLikeImg
                onClick={handleOnLikeArticle}
                src={fakeLikeSrc()}
              />
              <ArticlesLikesCount>{fakeLikeCounter()}</ArticlesLikesCount>
            </ArticlesStats>
          </ArticlesHeader>
          <ArticlesTagsWrap>
            {renderTagList()}
          </ArticlesTagsWrap>

          <ArticlesDescription>
            {description}
          </ArticlesDescription>
        </ArticlesInfo>

        <ArticlesUserInfoWrap>
          <ArticlesUserInfoGroup>
            <ArticlesUsername>{username}</ArticlesUsername>
            <ArticlesDate>{dateDiff}</ArticlesDate>
          </ArticlesUserInfoGroup>
          <ArticlesUserImage src={image} />
        </ArticlesUserInfoWrap>
      </ArticlesItem>
    );
  });
};

export default Article;
