import React from 'react';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import {
  signInUrl, signUpUrl, mainUrl, createArticleUrl, editProfileUrl,
} from '../../routes';
import { ArticlesUserImage, HeaderWrapper, HeaderIsAuth, HeaderUsername, HeaderIsNotAuth,HeaderSignIn, HeaderSignUp,HeaderCreateArticle,HeaderLogOut,HeaderEditProfile} from '../../styled-components';
import userDefaultImgUrl from '../../images/user-default-img.svg';
import * as actions from '../../redux/actions';
import { slide as Menu } from 'react-burger-menu';
/*import './mobile-menu.css';*/

var styles = {
  bmBurgerButton: {
    position: 'fixed',
    width: '36px',
    height: '30px',
    left: '36px',
    top: '36px'
  },
  bmBurgerBars: {
    background: '#373a47'
  },
  bmBurgerBarsHover: {
    background: '#a90000'
  },
  bmCrossButton: {
    height: '24px',
    width: '24px'
  },
  bmCross: {
    background: '#bdc3c7'
  },
  bmMenuWrap: {
    position: 'fixed',
    height: '100%'
  },
  bmMenu: {
    background: '#373a47',
    padding: '2.5em 1.5em 0',
    fontSize: '1.15em'
  },
  bmMorphShape: {
    fill: '#373a47'
  },
  bmItemList: {
    color: '#b8b7ad',
    padding: '0.8em'
  },
  bmItem: {
    display: 'inline-block'
  },
  bmOverlay: {
    background: 'rgba(0, 0, 0, 0.3)'
  }
}

const mapStateToProps = (state) => {
  const { isAuthorized, username, userProfileImage } = state.userData;
  return {
    isAuthorized,
    username,
    userProfileImage: (userProfileImage === undefined) ? userDefaultImgUrl : userProfileImage,
  };
};

const actionCreator = {
  logOutSuccess: actions.logOutSuccess,
};

const Header = (props) => {
  const {
    isAuthorized, username, userProfileImage, logOutSuccess,
  } = props;

  const handleOnLogOut = () => {
    logOutSuccess();
  };

  const renderHeaderMenu = () => {
    if (isAuthorized) {
      return (
        <HeaderIsAuth>
          <NavLink to={createArticleUrl}>
            <HeaderCreateArticle>
              Create article
            </HeaderCreateArticle>
          </NavLink>
          <NavLink to={editProfileUrl}>
            <HeaderEditProfile>
              <HeaderUsername>{username}</HeaderUsername>
              <ArticlesUserImage src={userProfileImage} />
            </HeaderEditProfile>
          </NavLink>
          <NavLink to={signUpUrl}>
            <HeaderLogOut onClick={handleOnLogOut}>
              Log Out
            </HeaderLogOut>
          </NavLink>
        </HeaderIsAuth>
      );
    }

    // если пользователь неавторизирован
    return (
      <HeaderIsNotAuth>
        <NavLink to={signInUrl}>
          <HeaderSignIn>Sign In</HeaderSignIn>
        </NavLink>
        <NavLink to={signUpUrl}>
          <HeaderSignUp>Sign Up</HeaderSignUp>
        </NavLink>
      </HeaderIsNotAuth>
    );
  };

  return (
    <HeaderWrapper>
      <NavLink to={mainUrl}>
        <HeaderSignIn>Realworld Blog</HeaderSignIn>
      </NavLink>
      <Menu right styles={styles}>
        {renderHeaderMenu()}
      </Menu>
    </HeaderWrapper>
  );
};

Header.propTypes = {
  isAuthorized: propTypes.bool,
  username: propTypes.string,
  userProfileImage: propTypes.string,
  logOutSuccess: propTypes.func,
};

Header.defaultProps = {
  isAuthorized: false,
  username: null,
  userProfileImage: null,
  logOutSuccess: null,
};

export default connect(mapStateToProps, actionCreator)(Header);
