import axios from 'axios';
import * as routes from './routes';
import { uniqueId } from 'lodash';

// проверка на ошибки в полях
export const checkFieldErrors = (errResponse, formik) => {
  Object.entries(errResponse).forEach((error) => {
    const [field, message] = error;
    formik.setFieldError(field, message[0]);
    formik.setSubmitting(false);
  });
};

// проверка на ошибку сети
export const networkErrorCheck = (err) => {
  if (err.message === 'Network Error') {
    alert('Ошибка соединения'); // eslint-disable-line no-alert
  }
};

// проверка ошибки авторизации
export const signInErrorsCheck = (errResponse, formik) => {
  if (Object.keys(errResponse).length > 0) {
    formik.setFieldError('email', 'Invalid login or password');
    formik.setFieldError('password', 'Invalid login or password');
    formik.setSubmitting(false);
  }
};



const mainRequestHandler = (req) => {
  req.headers['Content-Type'] = 'application/json;charset=utf-8';
  req.json = true;

  if (localStorage.token !== undefined) {
    req.headers['Authorization'] = `Token ${localStorage.token}`;
  }
  console.log(req);

  return req;
};

axios.interceptors.request.use(
  (req) => mainRequestHandler(req),
);

// авторизация
export const signInProcess = async (userData, formik) => {
  try {
    const response = await axios.post(routes.signInUrl, userData);
    const responseUserData = response.data.user;
    return {
      username: responseUserData.username,
      token: responseUserData.token,
      isAuthorized: true,
      userProfileImage: responseUserData.image,
    };
  } catch (error) {
    networkErrorCheck(error);
    if (error.response.status === 422) {
      signInErrorsCheck(error.response.data.errors, formik);
    }
    throw error;
  }
};

// регистрация
export const signUpProcess = async (newUser, formik) => {
  try {
    await axios.post(routes.signUpUrl, newUser);
    alert('Регистрация прошла успешно'); // eslint-disable-line no-alert
  } catch (error) {
    networkErrorCheck(error);
    if (error.response.status === 422) {
      checkFieldErrors(error.response.data.errors, formik);
    }
    throw error;
  }
};



// получаем список статей с помощью агрегации
export const getArticles = async (articlesPerRequest, articlesCounter) => {

  // counter увеличивается на кол-во полученных статей после каждого вызова функции из экшена
  const localRequestHandler = (req) => {
    req.params = { limit: articlesPerRequest, offset: articlesCounter };
    return req;
  };

  // инициализируем чтобы удалить после запроса
  const requestInterceptor = axios.interceptors.request.use(
    (req) => localRequestHandler(req),
  );

  try {
    const response = await axios.get(routes.getArticlesUrl);

    const { articles, articlesCount } = response.data;

    const initialArticlesById = articles.map((el) => ({
      ...el,
      id: uniqueId(),
      tagList: el.tagList.map((tagName) => ({ id: uniqueId(), title: tagName })),
    }));
    const articlesArray = [...initialArticlesById];

    // удаляем интерцептор
    axios.interceptors.request.eject(requestInterceptor);

    return {
      articles: articlesArray,
      articlesCount,
    };
  } catch (error) {
    console.log(error);
    throw error;
  }
}

