import React from 'react';
import styled from 'styled-components';
import { BrowserRouter as Router, NavLink, Route, Switch } from 'react-router-dom';
import ArticlesList from '../articles-list';
import SignIn from '../sign-in';
import SignUp from '../sign-up';
import EditProfile from '../edit-profile';


const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const Logo = styled.div`

`

const AuthButtonsWrap = styled.div`

`

const signInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
}

const signUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
}


const Header = () => {
  return (
    <HeaderWrapper>
      <Logo>Realworld Blog</Logo>
      <Router>
        <Switch>
          <Route exact path="/">
            <ArticlesList />
          </Route>
          <Route path="/sign-in">
            <SignIn />
          </Route>
          <Route path="/sign-up">
            <SignUp />
          </Route>
          <Route path="/edit-profile">
            <EditProfile />
          </Route>
        </Switch>
      </Router>
      <AuthButtonsWrap>
        <NavLink to="/sign-in" style={signInStyles}>Sign In</NavLink>
        <NavLink to="/sign-up" style={signUpStyles}>Sign Up</NavLink>
      </AuthButtonsWrap>
    </HeaderWrapper>
  )
}

export default Header;
