import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input, Checkbox } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import { Link } from 'react-router-dom';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';
import styled from 'styled-components';

const SignInWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Form = styled.form`
  max-width: 384px;
`;

const FormTitle = styled.div`
  text-align: center;
  color: #262626;
  font-size: 20px;
  margin: 0 0 16px 0;
  font-weight: 500;
`;

const FormItem = styled.label`
  color: #262626;
  font-size: 14px;
  font-weight: normal;
  margin: 0 0 16px 0;
  display: flex;
  flex-direction: column;

  &:last-of-type {
    margin-bottom: 8px;
  }
`;

const SignInLink = styled.div`
  color: #8C8C8C;
  font-size: 12px;
  text-align: center;
`;

const FormItemError = styled.div`
  color: #F5222D;
  font-size: 14px;
`;

const submitButtonStyles = {
  height: 40,
}

const CheckboxWrap = styled.div`
  border-top: 1px solid #E8E8E8;
  padding-top: 8px;
  display: flex;
  flex-direction: row;
`;

const formikInicialValues = {
  login: '',
  email: '',
  password: '',
  repeatPassword: '',
};

const formItems = [
  {
    id: uniqueId(), title: 'Username', name: 'login', placeholder: 'Username',
  },
  {
    id: uniqueId(), title: 'Email address', name: 'email', placeholder: 'Email',
  },
  {
    id: uniqueId(), title: 'Password', name: 'password', placeholder: 'Password',
  },
  {
    id: uniqueId(), title: 'Repeat Password', name: 'repeatPassword', placeholder: 'Repeat password',
  },
];

const formikValidationSchema = Yup.object({
  login: Yup.string()
    .max(30, 'Your username should be no more than 30 characters!')
    .required('required'),
  email: Yup.string()
    .email('Invalid email')
    .required('required'),
  password: Yup.string()
    .min(8, 'Your password need to be at least 6 characters.')
    .max(50, 'Your password should be no more than 50 characters.')
    .matches(/^[A-Za-z0-9]+$/, 'Password can be only with latin symbols and numbers')
    .matches(/(?=.*[0-9])/, 'Your password need to be at least 1 number')
    .matches(/(?=.*[A-Z])/, 'Your password need to be at least 1 uppercase symbol')
    .required('required'),
  repeatPassword: Yup.string()
    .oneOf([Yup.ref('password'), null], 'Passwords must match')
    .required('required'),
});

const mapStateToProps = (state) => {
  const { signUpState } = state;
  const isInputsDisable = (signUpState === 'requested');

  const props = { isInputsDisable, signUpState };
  return props;
};

const actionCreators = {
  signUp: actions.signUp,
};

const SignUp = (props) => {
  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      // eslint-disable-next-line no-use-before-define
      handleOnSignUp();
    },
  });
  const handleOnSignUp = async () => {
    const { signUp } = props;
    const { login, email, password } = formik.values;
    const newUser = {
      user: {
        username: login,
        email,
        password,
      },
    };
    signUp(newUser);
    formik.resetForm();
  };

  const { isInputsDisable } = props;

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={(formItem.name === 'password' || formItem.name === 'repeatPassword') ? 'password' : 'text'}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
          disabled={isInputsDisable}
        />
        {formik.touched[formItem.name] && formik.errors[formItem.name] ? (
          <FormItemError>{formik.errors[formItem.name]}</FormItemError>
        ) : null}
      </FormItem>
    ))
  );

  return (
    <Form onSubmit={formik.handleSubmit}>
      <FormTitle className="form-title">Create new account</FormTitle>

      {renderInputs()}

      <FormItem>
        <CheckboxWrap>
          <Checkbox id="acceptTerms" name="acceptTerms" />
          <label htmlFor="acceptTerms">I agree to the processing of my personal information</label>
        </CheckboxWrap>

      </FormItem>

      <FormItem>
        <Button
          htmlType="submit"
          type="primary"
          onClick={formik.handleSubmit}
          disabled={isInputsDisable}
          style={submitButtonStyles}
        >
          Create
        </Button>
      </FormItem>

      <SignInLink>
        <span>Already have account?</span>
        <Link to="sign-in"> Sign In.</Link>
      </SignInLink>
    </Form>
  );
};

SignUp.propTypes = {
  signUp: propTypes.func,
  isInputsDisable: propTypes.bool,
};

SignUp.defaultProps = {
  signUp: null,
  isInputsDisable: false,
};

export default connect(mapStateToProps, actionCreators)(SignUp);
