import React from 'react';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import { Link } from 'react-router-dom';
import propTypes from 'prop-types';
import { EyeInvisibleOutlined, EyeTwoTone } from '@ant-design/icons';
import 'antd/dist/antd.css';
import styled from 'styled-components';
import * as actions from '../../redux/actions';

const SignInWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Form = styled.form`
  max-width: 384px;
`;

const FormTitle = styled.div`
  text-align: center;
  color: #262626;
  font-size: 20px;
  margin: 0 0 16px 0;
  font-weight: 500;
`;

const FormItem = styled.label`
  color: #262626;
  font-size: 14px;
  font-weight: normal;
  margin: 0 0 16px 0;
`;

const formikInitialValues = {
  email: '',
  password: '',
};

const mapStateToProps = (state) => {
  const { signInState } = state;
  const isInputsDisable = (signInState === 'requested');

  return { isInputsDisable };
};

const actionCreators = {
  signIn: actions.signIn,
};

const SignIn = (props) => {
  const { isInputsDisable, signIn } = props;

  const handleOnAuthorizationUser = () => {
    // eslint-disable-next-line no-use-before-define
    const { email, password } = formik.values;
    const userData = {
      user: {
        email,
        password,
      },
    };
    signIn(userData);
  };

  const formik = useFormik({
    initialValues: formikInitialValues,
    onSubmit: () => {
      handleOnAuthorizationUser();
    },
  });

  return (
    <SignInWrapper>
      <Form onSubmit={formik.handleSubmit}>
        <FormTitle>Sign In</FormTitle>
        <FormItem htmlFor="email">
          Email address:
          <Input
            placeholder="Email address"
            id="email"
            name="email"
            onChange={formik.handleChange}
            value={formik.values.email}
            disabled={isInputsDisable}
          />
        </FormItem>

        <FormItem htmlFor="password">
          Password:
          <Input.Password
            id="password"
            name="password"
            placeholder="Password"
            onChange={formik.handleChange}
            value={formik.values.password}
            iconRender={(visible) => (visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />)}
            disabled={isInputsDisable}
          />
        </FormItem>

        <FormItem>
          <Button
            type="primary"
            htmlType="submit"
            className="form-submit"
            onClick={formik.handleSubmit}
          >
            Войти
          </Button>
        </FormItem>
        <span>
          Don't have account?
          <Link to="sign-up">Sign Up.</Link>
        </span>
      </Form>
    </SignInWrapper>

  );
};

SignIn.propTypes = {
  signIn: propTypes.func,
  isInputsDisable: propTypes.bool,
};

SignIn.defaultProps = {
  signIn: null,
  isInputsDisable: false,
};

export default connect(mapStateToProps, actionCreators)(SignIn);
