import React, { useEffect } from 'react';
import propTypes from 'prop-types';
import { connect } from 'react-redux';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from 'react-router-dom';
import * as actions from '../redux/actions';
import { signInSuccess } from '../redux/actions';
import PrivateRoute from '../react-router/private-route';
import AuthRoute from '../react-router/auth-route';
import Header from './header';
import SignIn from './sign-in';
import SignUp from './sign-up';
import EditProfile from './edit-profile';
import ArticlesList from './articles-list';
import Article from './article';
import EditArticle from './edit-article';
import CreateArticle from './create-article';
import {
  MainWrapper, ContentHeader, ContentWrapper, ContentBody, ContentAuth,
} from '../styled-components';
import {
  mainUrl,
  createArticleUrl,
  signInUrl,
  signUpUrl,
  articleUrl,
  editArticleUrl,
  editProfileUrl,
} from '../routes';

const mapStateToProps = (state) => {
  const { userData } = state;
  const props = {
    isAuthorized: userData.isAuthorized,
    userData,
  };
  return props;
};

const actionCreators = {
  signInSuccess: actions.signInSuccess,
};

const App = (props) => {
  const { dispatch, isAuthorized } = props;
  const authorizationCheck = () => {
    if (localStorage.token === undefined) return;
    dispatch(signInSuccess({
      isAuthorized: true,
      token: localStorage.token,
      username: localStorage.username,
    }));
  };

  useEffect(() => {
    authorizationCheck();
  }, [isAuthorized]);

  return (
    <MainWrapper>
      <Router>
        <ContentHeader>
          <Header />
        </ContentHeader>
        <ContentWrapper>
          <Switch>
            <Route exact path={mainUrl} component={ArticlesList}>
              <ContentBody>
                <ArticlesList />
              </ContentBody>
            </Route>
            <Route path={signInUrl} component={SignIn}>
              <ContentAuth>
                <SignIn />
              </ContentAuth>
            </Route>
            <Route path={signUpUrl} component={SignUp}>
              <ContentAuth>
                <SignUp />
              </ContentAuth>
            </Route>
            <PrivateRoute path={editProfileUrl} component={EditProfile}>
              <ContentBody>
                <EditProfile />
              </ContentBody>
            </PrivateRoute>
            <PrivateRoute path={createArticleUrl} component={CreateArticle}>
              <ContentBody>
                <CreateArticle />
              </ContentBody>
            </PrivateRoute>
            <PrivateRoute path={editArticleUrl} component={EditArticle}>
              <ContentBody>
                <EditArticle />
              </ContentBody>
            </PrivateRoute>
            <Route path={articleUrl} component={Article}>
              <ContentBody>
                <Article />
              </ContentBody>
            </Route>
          </Switch>
        </ContentWrapper>
      </Router>
    </MainWrapper>
  );
};

App.propTypes = {
  dispatch: propTypes.func,
  isAuthorized: propTypes.bool,
};

App.defaultProps = {
  dispatch: null,
  isAuthorized: false,
};

export default connect(mapStateToProps, actionCreators)(App);
