import { handleActions } from 'redux-actions';
import * as actions from '../actions';

export const signUpState = handleActions({
  [actions.signUpRequest]() {
    return 'requested';
  },
  [actions.signUpFailure]() {
    return 'failed';
  },
  [actions.signUpSuccess]() {
    return 'finished';
  },
}, 'none');

export const signInState = handleActions({
  [actions.signInRequest]() {
    return 'requested';
  },
  [actions.signInFailure]() {
    return 'failed';
  },
  [actions.signInSuccess]() {
    return 'finished';
  },
}, 'none');

export const editUserProfileState = handleActions({
  [actions.editUserProfileRequest]() {
    return 'requested';
  },
  [actions.editUserProfileFailure]() {
    return 'failed';
  },
  [actions.editUserProfileSuccess]() {
    return 'finished';
  },
}, 'none');

export const getArticlesState = handleActions({
  [actions.getArticlesProcess]() {
    return 'processed';
  },
  [actions.refreshArticlesRequest]() {
    return 'requested';
  },
  [actions.getArticlesSuccess]() {
    return 'finished';
  },
}, 'none');

export const createArticleState = handleActions({
  [actions.createArticleRequest]() {
    return 'requested';
  },
  [actions.createArticleFailure]() {
    return 'failed';
  },
  [actions.createArticleSuccess]() {
    return 'finished';
  },
}, 'none');

export const oneArticleState = handleActions({
  [actions.getOneArticleRequest]() {
    return 'requested';
  },
  [actions.getOneArticleFailure]() {
    return 'failed';
  },
  [actions.getOneArticleSuccess]() {
    return 'finished';
  },
}, 'none');

export const editArticleState = handleActions({
  [actions.editArticleRequest]() {
    return 'requested';
  },
  [actions.editArticleFailure]() {
    return 'failed';
  },
  [actions.editArticleSuccess]() {
    return 'finished';
  },
}, 'none');

export const addLikeRequestState = handleActions({
  [actions.addLikeRequest](state, action) {
    return [...state, action.payload];
  },
  [actions.addLikeFailure]() {
    return 'failed';
  },
  [actions.addLikeSuccess](state, action) {
    return [...state.filter((el) => el.id !== action.payload.id)];
  },
}, []);
