import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getDate } from '../../helper';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats,
  ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfoWrap, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';

const Article = (props) => {
  const { articles } = props;

  const [fakeLikes, setFakeLikes] = useState([
    { id: articles[0].id, isLiked: articles[0].favorited },
    { id: articles[1].id, isLiked: articles[1].favorited },
    { id: articles[2].id, isLiked: articles[2].favorited },
    { id: articles[3].id, isLiked: articles[3].favorited },
    { id: articles[4].id, isLiked: articles[4].favorited },
  ]);

  const renderArticle = () => {
    articles.map((article) => {
      const {
        author, createdAt, description, favoritesCount, favorited, id, tagList, title, slug,
      } = article;
      const { username, image } = author;
      const dateDiff = getDate(createdAt);

      console.log(fakeLikes.filter((el) => el.id === id));
      const renderTagList = () => (
        tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
      );

      const fakeLike = fakeLikes.filter((el) => el.id === id).favorited;
      const handleOnLikeArticle = () => {
        setFakeLikes((state) => {
          const newState = state.map((el) => {
            if (el.id === id) return { ...el, isLiked: !el.isLiked };
            return el;
          });
          console.log(newState);
          return newState;
        });
      };

      return (
        <ArticlesItem key={id}>
          <ArticlesInfo>
            <ArticlesHeader>
              <Link style={articlesTitle} to={articleUrl(slug)}>
                {title}
              </Link>
              <ArticlesStats>
                <ArtickesLikeImg
                  onClick={handleOnLikeArticle}
                  src={fakeLike ? activeLikeSrc : disabledLikeSrc}
                />
                <ArticlesLikesCount>{favoritesCount}</ArticlesLikesCount>
              </ArticlesStats>
            </ArticlesHeader>
            <ArticlesTagsWrap>
              {renderTagList()}
            </ArticlesTagsWrap>

            <ArticlesDescription>
              {description}
            </ArticlesDescription>
          </ArticlesInfo>

          <ArticlesUserInfoWrap>
            <ArticlesUserInfoGroup>
              <ArticlesUsername>{username}</ArticlesUsername>
              <ArticlesDate>{dateDiff}</ArticlesDate>
            </ArticlesUserInfoGroup>
            <ArticlesUserImage src={image} />
          </ArticlesUserInfoWrap>
        </ArticlesItem>
      );
    });
  };

  useEffect(() => {
    renderArticle();
  }, [fakeLikes]);

  return renderArticle();
};

export default Article;
