import React, { useEffect } from 'react';
import propTypes from 'prop-types';
import { connect } from 'react-redux';
import styled from 'styled-components';
import * as actions from '../redux/actions';
import { signInSuccess } from '../redux/actions';
import Header from './header';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from 'react-router-dom';
import SignIn from './sign-in';
import SignUp from './sign-up';
import EditProfile from './edit-profile';
import ArticlesList from './articles-list';

const MainWrapper = styled.div`
  background-color: #E5E5E5;
`;

const ContentHeader = styled.header`

`;

const ContentWrapper = styled.div`
  min-height: calc(100vh - 80px);
`;

const ContentBody = styled.div`
  background-color: #FFFFFF;
  margin: 24px 24px;
  max-width: 941px;
  padding: 16px;
`;

const ContentAuth= styled.div`
  margin: 60px auto;
  padding: 48px 32px;
  background: #FFFFFF;
  border: 1px solid #D9D9D9;
  box-sizing: border-box;
  box-shadow: 0px 0.608796px 2.93329px rgba(0, 0, 0, 0.0196802),
              0px 1.46302px 7.04911px rgba(0, 0, 0, 0.0282725), 
              0px 2.75474px 13.2728px rgba(0, 0, 0, 0.035), 
              0px 4.91399px 23.6765px rgba(0, 0, 0, 0.0417275), 
              0px 9.19107px 44.2843px rgba(0, 0, 0, 0.0503198), 
              0px 22px 106px rgba(0, 0, 0, 0.07);
  border-radius: 6px;
  max-width: 384px;
`;

const mapStateToProps = (state) => {
  const { userData } = state;
  const props = {
    isAuthorized: userData.isAuthorized,
    userData,
  };
  return props;
};

const actionCreators = {
  signInSuccess: actions.signInSuccess,
};

const App = (props) => {
  const { dispatch, isAuthorized } = props;
  const authorizationCheck = () => {
    if (localStorage.token === undefined) return;
    dispatch(signInSuccess({
      isAuthorized: true,
      token: localStorage.token,
      username: localStorage.username,
    }));
  };

  useEffect(() => {
    authorizationCheck();
  }, [isAuthorized]);

  return (
    <MainWrapper>
      <ContentHeader>
        <Header />
      </ContentHeader>
      <ContentWrapper>
        <Router>
          <Switch>
            <Route path="/">
              <ContentBody>
                <ArticlesList />
              </ContentBody>
            </Route>
            <Route path="/sign-in">
              <ContentAuth>
                <SignIn />
              </ContentAuth>
            </Route>
            <Route path="/sign-up">
              <ContentAuth>
                <SignUp />
              </ContentAuth>
            </Route>
            <Route path="/edit-profile">
              <ContentAuth>
                <EditProfile />
              </ContentAuth>
            </Route>

          </Switch>
        </Router>
      </ContentWrapper>
    </MainWrapper>
  );
};

App.propTypes = {
  dispatch: propTypes.func,
  isAuthorized: propTypes.bool,
};

App.defaultProps = {
  dispatch: null,
  isAuthorized: false,
};

export default connect(mapStateToProps, actionCreators)(App);
