import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input, Checkbox } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';
import {
  FormItem,
  FormArticle,
  FormTitle,
  FormSubmitButtonStyles,
  FormItemTags,
  FormAddTagButton,
  FormRemoveTagButton,
  FormAddTagInputStyles,
  FormItemTagsTitle,
} from '../../styled-components';
import { renderErrorMessage } from '../../helper';

const { TextArea } = Input;

const formikInicialValues = {
  title: '',
  description: '',
  text: '',
  tags: [],
};

const formItems = [
  {
    id: uniqueId(), title: 'Title', name: 'title', placeholder: 'Title', type: 'text',
  },
  {
    id: uniqueId(), title: 'Short description', name: 'description', placeholder: 'Description', type: 'text',
  },
];

const formikValidationSchema = Yup.object({
  title: Yup.string()
    .required('required'),
  description: Yup.string()
    .required('required'),
  text: Yup.string()
    .required('required'),
});

const mapStateToProps = (state) => {

};

const actionCreators = {
  signUp: actions.signUp,
};

const CreateArticle = (props) => {
  const {  } = props;

  const handleOnCreateArticle = async (formik) => {

  };


  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      // eslint-disable-next-line no-use-before-define
      handleOnCreateArticle(formik);
    },
  });

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={formItem.type}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
        />
        {renderErrorMessage(formik, formItem.name)}
      </FormItem>
    ))
  );

  return (
    <FormArticle onSubmit={formik.handleSubmit}>
      <FormTitle>Create new article</FormTitle>

      {renderInputs()}

      <FormItem htmlFor="text">
        Text
        <TextArea
          name="text"
          id="text"
          placeholder="write article text"
          rows={6}
          style={{ resize: 'none' }}
        >

        </TextArea>
        {renderErrorMessage(formik, 'text')}
      </FormItem>

      <FormItemTags>
        <FormItemTagsTitle>Tags</FormItemTagsTitle>
        <Input
          name="tags"
          id="tags"
          placeholder="write tag"
          style={FormAddTagInputStyles}
        />
        <FormRemoveTagButton>Delete</FormRemoveTagButton>
        <FormAddTagButton>Add tag</FormAddTagButton>
      </FormItemTags>

      <FormItem>
        <Button
          htmlType="submit"
          type="primary"
          onClick={formik.handleSubmit}
          style={FormSubmitButtonStyles}
        >
          Send
        </Button>
      </FormItem>
    </FormArticle>
  );
};

CreateArticle.propTypes = {

};

CreateArticle.defaultProps = {

};

export default connect(mapStateToProps, actionCreators)(CreateArticle);
