import styled from 'styled-components';

const defaultButtonStyles = `
  font-size: 14px;
  padding: 4px 16px;
  color: #F5222D;
  border: 1px solid #F5222D;
  border-radius: 5px;
  background-color: white;
  font-weight: 500;
`;

/* app.js main styles */
export const MainWrapper = styled.div`
  background-color: #E5E5E5;
`;

export const ContentHeader = styled.header`

`;

export const ContentWrapper = styled.div`
  min-height: calc(100vh - 80px);
  display: flex;
  justify-content: center;
`;

export const ContentBody = styled.div`
  background-color: #E5E5E5;
  margin: 24px 24px;
  max-width: 941px;
  width: 100%;
  word-break: break-all;
`;

export const ContentAuth = styled.div`
  margin: 60px 24px;
  padding: 48px 32px;
  background: #FFFFFF;
  border: 1px solid #D9D9D9;
  box-sizing: border-box;
  box-shadow: 0px 0.608796px 2.93329px rgba(0, 0, 0, 0.0196802),
              0px 1.46302px 7.04911px rgba(0, 0, 0, 0.0282725), 
              0px 2.75474px 13.2728px rgba(0, 0, 0, 0.035), 
              0px 4.91399px 23.6765px rgba(0, 0, 0, 0.0417275), 
              0px 9.19107px 44.2843px rgba(0, 0, 0, 0.0503198), 
              0px 22px 106px rgba(0, 0, 0, 0.07);
  border-radius: 6px;
  max-width: 100%;
  width: 384px;
  height: min-content;
`;
/* app.js main styles */

/* articles styles */
export const ArticlesWrapper = styled.div`

`;

export const ArticlesItemsWrapper = styled.div`
  background: #E5E5E5;
  display: flex;
  flex-direction: column;
`;

export const ArticlesItem = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;

  background-color: #FFFFFF;
  padding: 16px;
  margin-bottom: 20px;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.15);
  border-radius: 6px;
  min-height: 14vh;

  @media only screen and (max-width: 320px) {
    flex-direction: column;
  }
`;

export const ArticleWrapper = styled.div`
  background-color: #FFFFFF;
  padding: 16px;
  box-shadow: 0px 4px 12px rgba(0, 0, 0, 0.15);

`;

export const ArticleHeader = styled.div`
  display: flex;
  flex-direction: row;
  margin-bottom: 20px;
`;

export const ArticleBody = styled.div`
  word-break: break-all;
  min-height: 60vh;
`;

export const ArticlesInfo = styled.div`
  display: flex;
  flex-grow: 1;
  overflow: hidden;
  flex-direction: column;
  flex-basis: 70%;
  margin-right: 16px;

  & > div, & > p {
    flex-basis: 100%;
  }

  @media only screen and (max-width: 320px) {
    justify-content: center;
    text-align: center;
  }
`;

export const ArticlesHeader = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;

  @media only screen and (max-width: 320px) {
    justify-content: center;
  }
`;

export const ArticlesHeaderTitle = styled.div`
  color: #1890FF;
  font-size: 20px;
`;

export const ArticlesStats = styled.div`

`;

export const ArticlesLikeImg = styled.img`
  width: 16px;
  height: 16px;
  margin: 0 0 0 8px;
  cursor: pointer;
`;

export const ArticlesLikesCount = styled.span`
  margin: 0 0 0 4px;
`;

export const ArticlesUserInfoWrap = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  align-items: flex-start;

  @media only screen and (max-width: 320px) {
    order: -1;
    justify-content: center;
  }
`;

export const ArticleUserInfoWrap = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

`;

export const ArticlesUserInfo = styled.div`
  display: flex;
  flex-direction: row;
`;

export const ArticlesTagsWrap = styled.div``;

export const ArticlesTag = styled.span`
