import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getDate } from '../../helper';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats,
  ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfoWrap, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';

const Article = (props) => {
  const { articles } = props;

  // делаем фейковые лайки для визуального обновления без лишних запросов на сервер
  const [fakeLikes, setFakeLikes] = useState(articles.map((el) => ({
    id: el.id, isLiked: el.favorited, likesCount: el.favoritesCount
  })));

  useEffect(() => {

  }, [fakeLikes]);

  return articles.map((article) => {
    const {
      author, createdAt, description, favoritesCount, favorited, id, tagList, title, slug,
    } = article;
    const { username, image } = author;
    const dateDiff = getDate(createdAt);

    const renderTagList = () => (
      tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
    );

    // визуальное изменение лайка для пользователя
    const [fakeLike] = fakeLikes.filter((el) => el.id === id);

    const handleOnLikeArticle = () => {
      setFakeLikes((state) => {
        const newState = state.map((el) => {
          if (el.id === id) {
            return {
              ...el,
              isLiked: !el.isLiked,
              likesCount: (el.isLiked) ? el.likesCount - 1 : el.likesCount + 1,
            };
          }
          return el;
        });
        console.log(newState);
        return newState;
      });
    };

    return (
      <ArticlesItem key={id}>
        <ArticlesInfo>
          <ArticlesHeader>
            <Link style={articlesTitle} to={articleUrl(slug)}>
              {title}
            </Link>
            <ArticlesStats>
              <ArtickesLikeImg
                onClick={handleOnLikeArticle}
                src={fakeLike.isLiked ? activeLikeSrc : disabledLikeSrc}
              />
              <ArticlesLikesCount>{fakeLike.likesCount}</ArticlesLikesCount>
            </ArticlesStats>
          </ArticlesHeader>
          <ArticlesTagsWrap>
            {renderTagList()}
          </ArticlesTagsWrap>

          <ArticlesDescription>
            {description}
          </ArticlesDescription>
        </ArticlesInfo>

        <ArticlesUserInfoWrap>
          <ArticlesUserInfoGroup>
            <ArticlesUsername>{username}</ArticlesUsername>
            <ArticlesDate>{dateDiff}</ArticlesDate>
          </ArticlesUserInfoGroup>
          <ArticlesUserImage src={image} />
        </ArticlesUserInfoWrap>
      </ArticlesItem>
    );
  });
};

export default Article;
