import React, { useEffect } from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { useHistory } from 'react-router';
import { mainUrl } from '../../routes';
import { Button, Input } from 'antd';
import propTypes from 'prop-types';
import { renderErrorMessage, renderLoader } from '../../helper';
import * as Yup from 'yup';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';

import {
  FormItem,
  FormArticle,
  FormTitle,
  FormItemTagsWrap,
  FormAddTagButton,
  FormRemoveTagButton,
  FormItemTagsTitle,
  FormItemTag, ArticlesTag,
} from '../../styled-components';
import { useParams } from 'react-router-dom';


const formSubmitButtonStyles = {
  height: 40,
  maxWidth: 320,
  width: '100%',
};

const formAddTagInputStyles = {
  maxWidth: 300,
  width: '100%',
  borderRadius: 4,
};

const markdownStyles = {
  flexGrow: 1,
}

const formItems = [
  {
    id: uniqueId(), title: 'Title', name: 'title', placeholder: 'Title', type: 'text',
  },
  {
    id: uniqueId(), title: 'Short description', name: 'description', placeholder: 'Description', type: 'text',
  },
];

const formikValidationSchema = Yup.object({
  title: Yup.string()
    .required('required'),
  description: Yup.string()
    .required('required'),
  body: Yup.string()
    .required('required'),
});

const formikInicialValues = {
  title: '',
  description: '',
  body: '',
  tagText: '',
  tagList: '',
};

const mapStateToProps = (state) => {
  const { articles, userData, oneArticle, oneArticleState, editArticleState } = state;

  const currentArticle = (articles.length === 0) ? oneArticle : articles;
  return {
    articles,
    username: userData.username,
    isOneArticleReady: (oneArticleState === 'finished'),
    isArticleChanged: editArticleState === 'finished',
    currentArticle,
  };
};

const actionCreators = {
  editArticle: actions.createArticle,
  getOneArticle: actions.getOneArticle,
};

const EditArticle = (props) => {
  const { isArticleChanged, articles, username, getOneArticle, currentArticle, isOneArticleReady } = props;
  const { slug } = useParams();
  const validSlug = slug.slice(1, slug.length);

  let history = useHistory();
  const redirectToHome = () => {
    history.push(mainUrl);
  };

  let article;
  // если пользователь переходит по URL, то грузим нужный article с сервера
  if (!Array.isArray(currentArticle)) {
    if (!isOneArticleReady) return renderLoader();
    article = currentArticle;
  }

  // если по ссылке со списка артайклов, то берем из массива нужный
  if (Array.isArray(currentArticle)) {
    article = currentArticle.find((el) => el.slug === validSlug);
  }

  const { author, description, tagList, title, body } = article;
  const isCurrentUserAuthor = (username === author.username);




  const handleOnEditArticle = async (formik) => {
    const { editArticle } = props;
    const { title, description, body, tagList } = formik.values;
    const tags = tagList.map((tag) => tag.value);

    const articleBody = {
      title, description, body, tagList: tags,
    };
    editArticle(articleBody, formik);
  }

  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      handleOnEditArticle(formik);
    },
  });

  const handleOnAddTag = () => {
    const { tagText, tagList } = formik.values;
    if (tagText.trim().length === 0) {
      formik.setFieldValue('tagText', '');
      return;
    }

    const initialNewTag = {
      id: uniqueId(),
      value: tagText,
    };

    const newTagsArray = [...tagList, initialNewTag];
    formik.setFieldValue('tagList', newTagsArray);
    formik.setFieldValue('tagText', '');
  };

  const handleOnRemoveTag = (id) => () => {
    const { tagList } = formik.values;

    const newTagsArray = tagList.filter((tag) => tag.id !== id);

    formik.setFieldValue('tags', newTagsArray);
  };

  const handleOnClearTagInput = () => {
    formik.setFieldValue('tagText', '');
  };

  const renderTags = () => {
    const { tagList } = formik.values;
    if (tagList.length === 0) return null;

    return tagList.map((tag) => {
      const { id, value } = tag;

      return (
        <FormItemTag key={id}>
          <Input
            style={formAddTagInputStyles}
            onChange={formik.handleChange}
            value={value}
          />
          <FormRemoveTagButton onClick={handleOnRemoveTag(id)}>Delete</FormRemoveTagButton>
        </FormItemTag>
      );
    });
  };

  useEffect(() => {
    if (articles.length === 0) {
      getOneArticle(validSlug);
    }
  }, []);

  useEffect(() => {
    if (isArticleChanged) {
      redirectToHome();
    }
  }, [isArticleChanged]);

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={formItem.type}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
        />
        {renderErrorMessage(formik, formItem.name)}
      </FormItem>
    ))
  );

  return (
    <FormArticle onSubmit={formik.handleSubmit}>
      <FormTitle>Edit Article</FormTitle>

      {renderInputs()}

      <FormItem htmlFor="body" style={markdownStyles}>
        Text
        <Input.TextArea
          name="body"
          id="body"
          onChange={formik.handleChange}
          value={formik.values.body}
          rows={8}
          allowClear
        />
        {renderErrorMessage(formik, 'body')}
      </FormItem>

      <FormItemTagsWrap>
        <FormItemTagsTitle>Tags</FormItemTagsTitle>
        {renderTags()}
        <FormItemTag>
          <Input
            name="tagText"
            id="tagText"
            placeholder="write tag"
            style={formAddTagInputStyles}
            onChange={formik.handleChange}
            value={formik.values.tagText}
          />
          <FormRemoveTagButton onClick={handleOnClearTagInput}>Delete</FormRemoveTagButton>
          <FormAddTagButton onClick={handleOnAddTag}>Add tag</FormAddTagButton>
        </FormItemTag>
      </FormItemTagsWrap>

      <Button
        htmlType="submit"
        type="primary"
        onClick={formik.handleSubmit}
        style={formSubmitButtonStyles}
      >
        Send
      </Button>
    </FormArticle>
  );
};

EditArticle.propTypes = {

};

EditArticle.defaultProps = {

};

export default connect(mapStateToProps, actionCreators)(EditArticle);
