import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import { signInUrl, signUpUrl, mainUrl } from '../../routes';

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const HeaderIsAuth = styled.div`

`;

const signInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
};

const signUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
};

const Header = () => (
  <HeaderWrapper>
    <NavLink to={mainUrl} style={signInStyles}>Realworld Blog</NavLink>
    <HeaderIsAuth>
      <NavLink to={signInUrl} style={signInStyles}>Sign In</NavLink>
      <NavLink to={signUpUrl} style={signUpStyles}>Sign Up</NavLink>
    </HeaderIsAuth>
  </HeaderWrapper>
);

export default connect()(Header);
