import React from 'react';
import { Link, useParams } from 'react-router-dom';
import { getDate } from '../../helper';
import { connect } from 'react-redux';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats, ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfo, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import disabledLikeSrc from '../../images/like-disabled.svg';
import userDefaultImgSrc from '../../images/user-default-img.svg';
import activeLikeSrc from '../../images/like-active.svg';



const mapStateToProps = (state) => {
  const { articles } = state;

  return { articles };
}


const Article = (props) => {
  const { articles } = props;
  const { slug } = useParams();
  const validSlug = slug.slice(1, slug.length);
  const currentArticle = articles.find((article) => article.slug === validSlug);

  const { author, createdAt, description, favoritesCount, favorited, id, tagList, title } = currentArticle;
  const { username, image } = author;
  const dateDiff = getDate(createdAt);

  const renderTagList = () => (
    tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
  );

  return (
    <ArticlesItem key={id}>
      <ArticlesInfo>
        <ArticlesHeader>
          <Link style={articlesTitle} to={articleUrl(slug)}>
            {title}
          </Link>
          <ArticlesStats>
            <ArtickesLikeImg src={favorited ? activeLikeSrc : disabledLikeSrc}/>
            <ArticlesLikesCount>{favoritesCount}</ArticlesLikesCount>
          </ArticlesStats>
        </ArticlesHeader>
        <ArticlesTagsWrap>
          {renderTagList()}
        </ArticlesTagsWrap>

        <ArticlesDescription>
          {description}
        </ArticlesDescription>
      </ArticlesInfo>

      <ArticlesUserInfo>
        <ArticlesUserInfoGroup>
          <ArticlesUsername>{username}</ArticlesUsername>
          <ArticlesDate>{dateDiff}</ArticlesDate>
        </ArticlesUserInfoGroup>
        <ArticlesUserImage src={image}/>
      </ArticlesUserInfo>
    </ArticlesItem>
  );
};

export default connect(mapStateToProps, null)(Article);
