import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const Logo = styled.div`

`;

const HeaderIsAuth = styled.div`

`;

const signInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
};

const signUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
};

const Header = () => (
  <HeaderWrapper>
    <NavLink to="/" style={signInStyles}><Logo>Realworld Blog</Logo></NavLink>
    <HeaderIsAuth>
      <NavLink to="/sign-in" style={signInStyles}>Sign In</NavLink>
      <NavLink to="/sign-up" style={signUpStyles}>Sign Up</NavLink>
    </HeaderIsAuth>
  </HeaderWrapper>
);

export default connect()(Header);
