import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import { signInUrl, signUpUrl, mainUrl } from '../../routes';
import propTypes from 'prop-types';

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const HeaderIsAuth = styled.div`

`;

const HeaderIsNotAuth = styled.div`

`;

const signInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
};

const signUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
};

const mapStateToProps = (state) => {
  const { isAuthorized, username } = state.userData;

  return { isAuthorized, username };
}

const actionCreator = {

}

const Header = (props) => {
  const { isAuthorized, username } = props;
  const renderHeaderMenu = () => {
    if (isAuthorized) return (
      <div>yeeeeeeeeee</div>
    )
    return (
      <HeaderIsAuth>
        <NavLink to={signInUrl} style={signInStyles}>Sign In</NavLink>
        <NavLink to={signUpUrl} style={signUpStyles}>Sign Up</NavLink>
      </HeaderIsAuth>
    )
  }

  return (
    <HeaderWrapper>
      <NavLink to={mainUrl} style={signInStyles}>Realworld Blog</NavLink>
      {renderHeaderMenu()}
    </HeaderWrapper>
  )
}

Header.propTypes = {
  isAuthorized: propTypes.bool,
  username: propTypes.string,
}

Header.defaultProps = {
  isAuthorized: false,
  username: '',
}

export default connect(mapStateToProps, actionCreator)(Header);
