import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';

import {
  Form, FormTitle, FormItem, FormSubmitButtonStyles,
} from '../../styled-components';
import { renderErrorMessage } from '../../helper';

const formikInicialValues = {
  username: '',
  email: '',
  password: '',
  image: '',
};

const formItems = [
  {
    id: uniqueId(), title: 'Username', name: 'username', placeholder: 'Username',
  },
  {
    id: uniqueId(), title: 'Email address', name: 'email', placeholder: 'Email',
  },
  {
    id: uniqueId(), title: 'New Password', name: 'password', placeholder: 'New Password',
  },
  {
    id: uniqueId(), title: 'Avatar image (url)', name: 'image', placeholder: 'url',
  },
];

const formikValidationSchema = Yup.object({
  username: Yup.string()
    .max(30, 'Your username should be no more than 30 characters!')
    .required('required'),
  email: Yup.string()
    .email('Invalid email')
    .required('required'),
  password: Yup.string()
    .min(8, 'Your password need to be at least 6 characters.')
    .max(50, 'Your password should be no more than 50 characters.')
    .matches(/^[A-Za-z0-9]+$/, 'Password can be only with latin symbols and numbers')
    .matches(/(?=.*[0-9])/, 'Your password need to be at least 1 number')
    .matches(/(?=.*[A-Z])/, 'Your password need to be at least 1 uppercase symbol')
    .required('required'),
  image: Yup.string()
    .url('Not correct url')
    .required('required'),
});

const actionCreator = {

}

const EditProfile = (props) => {

  const onEditProfile = (formik) => {

  }

  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      onEditProfile(formik);
    },
  });

  const { isInputsDisable } = props;

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={(formItem.name === 'password') ? 'password' : 'text'}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
          disabled={isInputsDisable}
        />
        {renderErrorMessage(formik, formItem.name)}
      </FormItem>
    ))
  );

  return (
    <Form onSubmit={formik.handleSubmit}>
      <FormTitle>Edit Profile</FormTitle>

      {renderInputs()}

      <FormItem>
        <Button
          htmlType="submit"
          type="primary"
          onClick={formik.handleSubmit}
          disabled={isInputsDisable}
          style={FormSubmitButtonStyles}
        >
          Save
        </Button>
      </FormItem>
    </Form>
  );
};

EditProfile.propTypes = {
  isInputsDisable: propTypes.bool,
};

EditProfile.defaultProps = {
  isInputsDisable: false,
};

export default connect()(EditProfile);
