import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { getDate } from '../../helper';
import * as actions from '../../redux/actions';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesItem, ArticlesLikesCount,
  ArticlesStats,
  ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfoWrap, ArticlesUserInfoGroup, ArticlesUsername,
} from '../../styled-components';
import { articleUrl } from '../../routes';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';

const mapStateToProps = (state) => {
  const { likeState, addLikeRequestState } = state;
  console.log(state);

  const isLikeChangedOnServer = addLikeRequestState === 'finished';
  const isLikeChangeAlreadyRequested = addLikeRequestState === 'requested';

  return { likeState, isLikeChangedOnServer, isLikeChangeAlreadyRequested };
};

const actionCreator = {
  addLike: actions.addLike,
};

const Article = (props) => {
  const {
    articles, fakeLikes, setFakeLikes, likeState, isLikeChangedOnServer, isLikeChangeAlreadyRequested, addLike,
  } = props;

  return articles.map((article) => {
    const {
      author, createdAt, description, id, tagList, title, slug,
    } = article;
    const { username, image } = author;
    const dateDiff = getDate(createdAt);

    const renderTagList = () => (
      tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
    );

    // визуальное изменение лайка для пользователя
    const getFakeLike = () => {
      if (fakeLikes === undefined) return null;
      return fakeLikes.filter((el) => el.id === id)[0];
    };

    // рендер лайка
    const fakeLikeSrc = () => {
      if (getFakeLike() === undefined) return disabledLikeSrc;
      return getFakeLike().isLiked ? activeLikeSrc : disabledLikeSrc;
    };

    // рендер счетчика лайков
    const fakeLikeCounter = () => {
      if (getFakeLike() === undefined) return 0;
      return getFakeLike().likesCount;
    };
    /*
        1) пользователь нажимает на фейк-лайк
        2) отправляется запрос на сервер с измененным фейк-состоянием
           если пользователь нажимает на лайк пока запрос идет, то повторный запрос не отправляется
        3) приходит ответ с изменённым состоянием
           после ответа проверяем, состояние фейк-лайка такое же как и у обновлённого состояния?
           если да, то оставляем всё как есть
           если нет, то снова отправляем запрос на сервер
    */
    const handleOnLikeArticle = () => {

      setFakeLikes((state) => {
        const newState = state.map((el) => {
          if (el.id === id) {
            return {
              ...el,
              isLiked: !el.isLiked,
              likesCount: (el.isLiked) ? el.likesCount - 1 : el.likesCount + 1,
            };
          }
          return el;
        });
        const fakeLikeState = newState.filter((el) => el.id === id)[0].isLiked;

        // все еще идет прошлый запрос на изменение лайка
        if (isLikeChangeAlreadyRequested) {
          console.log('отправка запрещена');
          return false;
        }

        // отправляем новый
        console.log('отправляем новый');
        console.log('возвращенный state', likeState);
        console.log('текущий state', getFakeLike().isLiked);
        if (likeState !== fakeLikeState) {
          addLike(fakeLikeState, slug);
        };
        return newState;
      });
    };

    return (
      <ArticlesItem key={id}>
        <ArticlesInfo>
          <ArticlesHeader>
            <Link style={articlesTitle} to={articleUrl(slug)}>
              {title}
            </Link>
            <ArticlesStats>
              <ArtickesLikeImg
                onClick={handleOnLikeArticle}
                src={fakeLikeSrc()}
              />
              <ArticlesLikesCount>{fakeLikeCounter()}</ArticlesLikesCount>
            </ArticlesStats>
          </ArticlesHeader>
          <ArticlesTagsWrap>
            {renderTagList()}
          </ArticlesTagsWrap>

          <ArticlesDescription>
            {description}
          </ArticlesDescription>
        </ArticlesInfo>

        <ArticlesUserInfoWrap>
          <ArticlesUserInfoGroup>
            <ArticlesUsername>{username}</ArticlesUsername>
            <ArticlesDate>{dateDiff}</ArticlesDate>
          </ArticlesUserInfoGroup>
          <ArticlesUserImage src={image} />
        </ArticlesUserInfoWrap>
      </ArticlesItem>
    );
  });
};

export default connect(mapStateToProps, actionCreator)(Article);
