import React, { useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../redux/actions';
import { articleUrl } from '../../routes';
import disabledLikeSrc from '../../images/like-disabled.svg';
import activeLikeSrc from '../../images/like-active.svg';
import { renderLoader, getDate } from '../../helper';
import ReactMarkdown from 'react-markdown';
import {
  ArtickesLikeImg, ArticlesDate, ArticlesDescription,
  ArticlesHeader,
  ArticlesInfo, ArticlesLikesCount,
  ArticlesStats, ArticlesTag, ArticlesTagsWrap,
  articlesTitle, ArticlesUserImage, ArticlesUserInfo, ArticlesUserInfoGroup, ArticlesUsername,
  ArticleAuthorOptions, ArticleDeleteButton, ArticleEditButton, ArticlesUserInfoWrap,
  ArticleWrapper, ArticleHeader, ArticleBody
} from '../../styled-components';

const mapStateToProps = (state) => {
  const { articles, userData, oneArticle, oneArticleState } = state;

  console.log(state);
  return {
    articles,
    username: userData.username,
    isOneArticleReady: (oneArticleState === 'finished'),
    oneArticle,
  };
}

const actionCreator = {
  getOneArticle: actions.getOneArticle,
}


const Article = (props) => {
  const { articles, username, getOneArticle, oneArticle, isOneArticleReady} = props;
  const { slug } = useParams();
  const validSlug = slug.slice(1, slug.length);



  useEffect(() => {
    if (articles.length === 0) {
      getOneArticle(validSlug);
    }
  }, []);


  const renderArticle = (data) => {
    let article;

    // если пользователь переходит по URL, то грузим нужный article с сервера
    if (!Array.isArray(data)) {
      if (!isOneArticleReady) return renderLoader();
      article = data;
    }

    // если по ссылке со списка артайклов, то берем из массива нужный
    if (Array.isArray(data)) {
      article = data.find((el) => el.slug === validSlug);
    }

    const { author, createdAt, description, favoritesCount, favorited, tagList, title, body } = article;
    const isCurrentUserAuthor = (username === author.username);
    console.log(body);

    const dateDiff = getDate(createdAt);

    const renderTagList = () => (
      tagList.map((tag) => <ArticlesTag key={tag.id}>{tag.title}</ArticlesTag>)
    );

    const renderAuthorOptions = () => {
      if (!isCurrentUserAuthor) return false;
      return (
        <ArticleAuthorOptions>
          <ArticleDeleteButton>test</ArticleDeleteButton>
          <ArticleEditButton>test</ArticleEditButton>
        </ArticleAuthorOptions>
      );
    };

    return (
      <ArticleWrapper>
        <ArticleHeader>
          <ArticlesInfo>
            <ArticlesHeader>
              <Link style={articlesTitle} to={articleUrl(slug)}>
                {title}
              </Link>
              <ArticlesStats>
                <ArtickesLikeImg src={favorited ? activeLikeSrc : disabledLikeSrc} />
                <ArticlesLikesCount>{favoritesCount}</ArticlesLikesCount>
              </ArticlesStats>
            </ArticlesHeader>
            <ArticlesTagsWrap>
              {renderTagList()}
            </ArticlesTagsWrap>

            <ArticlesDescription>
              {description}
            </ArticlesDescription>
          </ArticlesInfo>

          <ArticlesUserInfoWrap>
            <ArticlesUserInfo>
              <ArticlesUserInfoGroup>
                <ArticlesUsername>{author.username}</ArticlesUsername>
                <ArticlesDate>{dateDiff}</ArticlesDate>
              </ArticlesUserInfoGroup>
              <ArticlesUserImage src={author.image} />
            </ArticlesUserInfo>
            {renderAuthorOptions()}
          </ArticlesUserInfoWrap>
        </ArticleHeader>

        <ArticleBody>
          <ReactMarkdown
            name="body"
            id="body"
            sourse={body}
          />
        </ArticleBody>
      </ArticleWrapper>
    );
  };

  return (articles.length === 0) ? renderArticle(oneArticle) : renderArticle(articles);
};

export default connect(mapStateToProps, actionCreator)(Article);
