import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';
import styled from 'styled-components';

const Form = styled.form`
  max-width: 384px;
`;

const FormTitle = styled.div`
  text-align: center;
  color: #262626;
  font-size: 20px;
  margin: 0 0 16px 0;
  font-weight: 500;
`;

const FormItem = styled.label`
  color: #262626;
  font-size: 14px;
  font-weight: normal;
  margin: 0 0 16px 0;
  display: flex;
  flex-direction: column;

  &:last-of-type {
    margin-bottom: 8px;
  }
`;

const FormItemError = styled.div`
  color: #F5222D;
  font-size: 14px;
`;

const submitButtonStyles = {
  height: 40,
}

const formikInicialValues = {
  login: '',
  email: '',
  newPassword: '',
  avatarUrl: '',
};

const formItems = [
  {
    id: uniqueId(), title: 'Username', name: 'login', placeholder: 'Username',
  },
  {
    id: uniqueId(), title: 'Email address', name: 'email', placeholder: 'Email',
  },
  {
    id: uniqueId(), title: 'New Password', name: 'newPassword', placeholder: 'New Password',
  },
  {
    id: uniqueId(), title: 'Avatar image (url)', name: 'avatarUrl', placeholder: 'url',
  },
];

const formikValidationSchema = Yup.object({
  login: Yup.string()
    .max(30, 'Your username should be no more than 30 characters!')
    .required('required'),
  email: Yup.string()
    .email('Invalid email')
    .required('required'),
  newPassword: Yup.string()
    .min(8, 'Your password need to be at least 6 characters.')
    .max(50, 'Your password should be no more than 50 characters.')
    .matches(/^[A-Za-z0-9]+$/, 'Password can be only with latin symbols and numbers')
    .matches(/(?=.*[0-9])/, 'Your password need to be at least 1 number')
    .matches(/(?=.*[A-Z])/, 'Your password need to be at least 1 uppercase symbol')
    .required('required'),
  avatarUrl: Yup.string()
    .url('Not correct url')
    .required('required'),
});

const EditProfile = (props) => {
  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      // eslint-disable-next-line no-use-before-define
    },
  });

  const { isInputsDisable } = props;

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name}>
        {formItem.title}
        <Input
          type={(formItem.name === 'password') ? 'password' : 'text'}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
          disabled={isInputsDisable}
        />
        {formik.touched[formItem.name] && formik.errors[formItem.name] ? (
          <FormItemError>{formik.errors[formItem.name]}</FormItemError>
        ) : null}
      </FormItem>
    ))
  );

  return (
    <Form onSubmit={formik.handleSubmit}>
      <FormTitle>Edit Profile</FormTitle>

      {renderInputs()}

      <FormItem>
        <Button
          htmlType="submit"
          type="primary"
          onClick={formik.handleSubmit}
          disabled={isInputsDisable}
          style={submitButtonStyles}
        >
          Save
        </Button>
      </FormItem>
    </Form>
  );
};

EditProfile.propTypes = {
  isInputsDisable: propTypes.bool,
};

EditProfile.defaultProps = {
  isInputsDisable: false,
};

export default connect()(EditProfile);
