import React from 'react';
import { uniqueId } from 'lodash';
import { connect } from 'react-redux';
import { useFormik } from 'formik';
import { Button, Input } from 'antd';
import propTypes from 'prop-types';
import * as Yup from 'yup';
import { Link } from 'react-router-dom';
import 'antd/dist/antd.css';
import * as actions from '../../redux/actions';
import styled from 'styled-components';

const SignInWrapper = styled.div`
  display: flex;
  flex-direction: column;
`;

const Form = styled.form`
  max-width: 384px;
`;

const FormTitle = styled.div`
  text-align: center;
  color: #262626;
  font-size: 20px;
  margin: 0 0 16px 0;
  font-weight: 500;
`;

const FormItem = styled.label`
  color: #262626;
  font-size: 14px;
  font-weight: normal;
  margin: 0 0 16px 0;
  display: flex;
  flex-direction: column;

  &:last-of-type {
    margin-bottom: 8px;
  }
`;

const SignInLink = styled.div`
  color: #8C8C8C;
  font-size: 12px;
  text-align: center;
`;

const formikInicialValues = {
  login: '',
  email: '',
  password: '',
  repeatPassword: '',
};

const formItems = [
  {
    id: uniqueId(), title: 'Username', name: 'login', placeholder: 'Username',
  },
  {
    id: uniqueId(), title: 'Email address', name: 'email', placeholder: 'Email',
  },
  {
    id: uniqueId(), title: 'Password', name: 'password', placeholder: 'Password',
  },
  {
    id: uniqueId(), title: 'Repeat Password', name: 'repeatPassword', placeholder: 'Repeat password',
  },
];

const formikValidationSchema = Yup.object({
  login: Yup.string()
    .max(30, 'Too long!')
    .required('Заполните поле'),
  email: Yup.string()
    .email('Invalid email')
    .required('Заполните поле'),
  password: Yup.string()
    .min(8, 'Your password need to be at least 6 characters.')
    .max(50, 'Your password need to be not m')
    .matches(/^[A-Za-z0-9]+$/, 'Пароль может содержать только латинские символы и цифры')
    .matches(/(?=.*[0-9])/, 'Пароль должен содержать хотя бы одну цифру')
    .matches(/(?=.*[A-Z])/, 'Пароль должен содержать хотя бы одну заглавную букву')
    .required('Заполните поле'),
  repeatPassword: Yup.string()
    .oneOf([Yup.ref('password'), null], 'Пароли должны совпадать')
    .required('Заполните поле'),
});

const mapStateToProps = (state) => {
  const { signUpState } = state;
  const isInputsDisable = (signUpState === 'requested');

  const props = { isInputsDisable, signUpState };
  return props;
};

const actionCreators = {
  signUp: actions.signUp,
};

const SignUp = (props) => {
  const formik = useFormik({
    initialValues: formikInicialValues,
    validationSchema: formikValidationSchema,
    onSubmit: () => {
      // eslint-disable-next-line no-use-before-define
      handleOnSignUp();
    },
  });
  const handleOnSignUp = async () => {
    const { signUp } = props;
    const { login, email, password } = formik.values;
    const newUser = {
      user: {
        username: login,
        email,
        password,
      },
    };
    signUp(newUser);
    formik.resetForm();
  };

  const { isInputsDisable } = props;

  const renderInputs = () => (
    formItems.map((formItem) => (
      <FormItem key={formItem.id} htmlFor={formItem.name} className="form-item">
        {formItem.title}
        <Input
          type={(formItem.name === 'password' || formItem.name === 'repeatPassword') ? 'password' : 'text'}
          placeholder={formItem.placeholder}
          id={formItem.name}
          name={formItem.name}
          onChange={formik.handleChange}
          value={formik.values[formItem.name]}
          disabled={isInputsDisable}
        />
        {formik.touched[formItem.name] && formik.errors[formItem.name] ? (
          <div className="form-item-error">{formik.errors[formItem.name]}</div>
        ) : null}
      </FormItem>
    ))
  );

  return (
    <form onSubmit={formik.handleSubmit} className="form">
      <FormTitle className="form-title">Регистрация</FormTitle>
      {renderInputs()}
      <FormItem className="form-item">
        <Button
          htmlType="submit"
          type="primary"
          className="form-submit"
          onClick={formik.handleSubmit}
          disabled={isInputsDisable}
        >
          Зарегистрироваться
        </Button>
      </FormItem>
      <SignInLink>
        <span>Already have account?</span>
        <Link to="sign-in"> Sign In.</Link>
      </SignInLink>
    </form>
  );
};

SignUp.propTypes = {
  signUp: propTypes.func,
  isInputsDisable: propTypes.bool,
};

SignUp.defaultProps = {
  signUp: null,
  isInputsDisable: false,
};

export default connect(mapStateToProps, actionCreators)(SignUp);
