import React, { useEffect } from 'react';
import Loader from 'react-loader-spinner'
import { Pagination } from 'antd';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../redux/actions';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';
import userDefaultImgSrc from '../../images/user-default-img.svg';
import {
  ArticlesWrapper,
  PaginationWrapper,
  ArticlesDate,
  ArticlesUserImage,
  ArticlesUsername,
  ArticlesUserInfoGroup,
  ArticlesUserInfo,
  ArticlesDescription,
  ArticlesTag,
  ArticlesStats,
  ArticlesTagsWrap,
  ArticlesLikesCount,
  articlesTitle,
  ArtickesLikeImg,
  ArticlesItemsWrapper,
  ArticlesItem,
  ArticlesInfo,
  ArticlesHeader,
} from '../../styled-components';
import { articleUrl } from '../../routes';

const loaderStyles = {
  margin: '0px auto',
}

const mapStateToProps = (state) => {
  const { articles, getArticlesState } = state;
  console.log(state);
  return {
    articles,
    isArticlesReady: getArticlesState === 'finished',
  };
}

const actionCreator = {
  getArticles: actions.getArticles,
}

const ArticlesList = (props) => {
  const { getArticles, articles, isArticlesReady } = props;

  const getArticlesRequest = async () => {
    await getArticles();
  }

  useEffect(() => {
    getArticlesRequest();
  }, []);

  const renderLoader = () => (
    <Loader type="Circles" color="#00BFFF" height={80} width={80} style={loaderStyles} />
  )

  const renderArticles = () => (
    <ArticlesItem>
      <ArticlesInfo>
        <ArticlesHeader>
          <Link style={articlesTitle} to={articleUrl}>
            Some article title
          </Link>
          <ArticlesStats>
            <ArtickesLikeImg src={disabledLikeSrc} />
            <ArticlesLikesCount>10</ArticlesLikesCount>
          </ArticlesStats>
        </ArticlesHeader>
        <ArticlesTagsWrap>
          <ArticlesTag>tag</ArticlesTag>
          <ArticlesTag>someTag</ArticlesTag>
        </ArticlesTagsWrap>

        <ArticlesDescription>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit,
          sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
          Ut enim ad minim veniam, quis nostrud exercitation ullamco
          laboris nisi ut aliquip ex ea commodo consequat
        </ArticlesDescription>
      </ArticlesInfo>

      <ArticlesUserInfo>
        <ArticlesUserInfoGroup>
          <ArticlesUsername>John Doe</ArticlesUsername>
          <ArticlesDate>March 5, 2020</ArticlesDate>
        </ArticlesUserInfoGroup>
        <ArticlesUserImage src={userDefaultImgSrc} />
      </ArticlesUserInfo>
    </ArticlesItem>
  );
// {(isArticlesReady) ? renderArticles() : renderLoader() }
  return (
    <ArticlesWrapper>
      <ArticlesItemsWrapper>

        {renderLoader()}
      </ArticlesItemsWrapper>

      <PaginationWrapper>
        <Pagination
          size="small"
          total={100}
          pageSize={5}
          showSizeChanger={false}
        />
      </PaginationWrapper>
    </ArticlesWrapper>
  )
}

export default connect(mapStateToProps, actionCreator)(ArticlesList);
