import React, { useEffect } from 'react';
import Loader from 'react-loader-spinner'
import { Pagination } from 'antd';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import * as actions from '../../redux/actions';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';
import userDefaultImgSrc from '../../images/user-default-img.svg';
import {
  ArticlesWrapper,
  PaginationWrapper,
  ArticlesDate,
  ArticlesUserImage,
  ArticlesUsername,
  ArticlesUserInfoGroup,
  ArticlesUserInfo,
  ArticlesDescription,
  ArticlesTag,
  ArticlesStats,
  ArticlesTagsWrap,
  ArticlesLikesCount,
  articlesTitle,
  ArtickesLikeImg,
  ArticlesItemsWrapper,
  ArticlesItem,
  ArticlesInfo,
  ArticlesHeader,
  LoaderWrapper,
} from '../../styled-components';
import { articleUrl } from '../../routes';

const loaderStyles = {
  margin: '0px auto',
}

const mapStateToProps = (state) => {
  const { articles, getArticlesState } = state;
  console.log(state);
  return {
    articles,
    isArticlesReady: getArticlesState === 'finished',
    isRequestProcessed: getArticlesState === 'processed',
  };
}

const actionCreator = {
  getArticles: actions.getArticles,
}

const ArticlesList = (props) => {
  const { getArticles, articles, isArticlesReady, isRequestProcessed } = props;

  const getArticlesRequest = async () => {
    await getArticles();
  }

  const renderLoader = () => (
    <LoaderWrapper>
      <Loader type="Oval" color="rgb(82, 196, 26)" height={200} width={200} />
    </LoaderWrapper>
  );

  const handleOnChangePagination = (value, pageSize) => {
    console.log(value);
  }

  const renderPagination = () => (
    <Pagination
      size="small"
      total={500}
      pageSize={5}
      showSizeChanger={false}
      onChange={handleOnChangePagination}
    />
  );

  useEffect(() => {
    getArticlesRequest();
  }, []);

  const renderArticles = (pageNumber = 1, pageSize = 5) => {

    const currentArticlesArray = Array.from(articles).slice(pageNumber * pageSize - pageSize, pageNumber * pageSize);
    console.log(currentArticlesArray);
    return (
      <ArticlesItem>
        <ArticlesInfo>
          <ArticlesHeader>
            <Link style={articlesTitle} to={articleUrl}>
              Some article title
            </Link>
            <ArticlesStats>
              <ArtickesLikeImg src={disabledLikeSrc} />
              <ArticlesLikesCount>10</ArticlesLikesCount>
            </ArticlesStats>
          </ArticlesHeader>
          <ArticlesTagsWrap>
            <ArticlesTag>tag</ArticlesTag>
            <ArticlesTag>someTag</ArticlesTag>
          </ArticlesTagsWrap>

          <ArticlesDescription>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit,
            sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
            Ut enim ad minim veniam, quis nostrud exercitation ullamco
            laboris nisi ut aliquip ex ea commodo consequat
          </ArticlesDescription>
        </ArticlesInfo>

        <ArticlesUserInfo>
          <ArticlesUserInfoGroup>
            <ArticlesUsername>John Doe</ArticlesUsername>
            <ArticlesDate>March 5, 2020</ArticlesDate>
          </ArticlesUserInfoGroup>
          <ArticlesUserImage src={userDefaultImgSrc} />
        </ArticlesUserInfo>
      </ArticlesItem>
    );
  };

  return (
    <ArticlesWrapper>
      <ArticlesItemsWrapper>
        {(isRequestProcessed || isArticlesReady) ? renderArticles() : renderLoader() }
      </ArticlesItemsWrapper>

      <PaginationWrapper>
        {renderPagination()}
      </PaginationWrapper>
    </ArticlesWrapper>
  )
}

export default connect(mapStateToProps, actionCreator)(ArticlesList);
