import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import propTypes from 'prop-types';
import {
  signInUrl, signUpUrl, mainUrl, createArticleUrl, editProfileUrl,
} from '../../routes';
import { FormTitle, ArticlesUserImage } from '../../styled-components';
import userDefaultImgUrl from '../../images/user-default-img.svg';
import * as actions from '../../redux/actions';

const HeaderWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  height: 80px;
  font-size: 18px;
  align-items: center;
  padding: 0px 20px;
  background-color: #FFFFFF;
`;

const HeaderIsAuth = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;

  & > *:not(:last-child):not(:nth-child(2)) {
    margin-right: 24px;
  }

  & > div:nth-child(2) {
    padding: 6px 8px 0 0;
  }
`;

const HeaderUsername = styled.span`
  color: #262626;
  font-size: 20px;
  font-weight: 500;
  padding: 4px 8px 0 0;
`;

const HeaderIsNotAuth = styled.div`

`;

const headerSignInStyles = {
  marginRight: 32,
  textDecoration: 'none',
  color: 'rgba(0, 0, 0, 0.85)',
};

const headerSignUpStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '8px 16px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
};

const headerCreateArticleStyles = {
  border: '1px solid #52C41A',
  boxSizing: 'border-box',
  padding: '4px 8px',
  fontSize: '14px',
  borderRadius: 5,
  color: '#52C41A',
  textDecoration: 'none',
  display: 'flex',
  alignItems: 'center',
};

const headerLogOutStyles = {
  border: '1px solid rgba(0, 0, 0, 0.75)',
  boxSizing: 'border-box',
  borderRadius: '5px',
  fontSize: '18px',
  color: 'rgba(0, 0, 0, 0.75)',
  padding: '8px 16px',
};

const headerEditProfileStyles = {
  display: 'flex',
  flexDirection: 'row',
  margin: '0 16px 0 0',
}

const mapStateToProps = (state) => {
  const { isAuthorized, username, userProfileImage } = state.userData;
  return {
    isAuthorized,
    username,
    userProfileImage: (userProfileImage === undefined) ? userDefaultImgUrl : userProfileImage,
  };
};

const actionCreator = {
  logOutSuccess: actions.logOutSuccess,
};

const Header = (props) => {
  const {
    isAuthorized, username, userProfileImage, logOutSuccess,
  } = props;

  const handleOnLogOut = () => {
    logOutSuccess();
  };

  const renderHeaderMenu = () => {
    if (isAuthorized) {
      return (
        <HeaderIsAuth>
          <NavLink to={createArticleUrl} style={headerCreateArticleStyles}>Create article</NavLink>
          <NavLink to={editProfileUrl} style={headerEditProfileStyles}>
            <HeaderUsername>{username}</HeaderUsername>
            <ArticlesUserImage src={userProfileImage} />
          </NavLink>
          <NavLink to={signUpUrl} style={headerLogOutStyles} onClick={handleOnLogOut}>Log Out</NavLink>
        </HeaderIsAuth>
      );
    }

    // если пользователь неавторизирован
    return (
      <HeaderIsNotAuth>
        <NavLink to={signInUrl} style={headerSignInStyles}>Sign In</NavLink>
        <NavLink to={signUpUrl} style={headerSignUpStyles}>Sign Up</NavLink>
      </HeaderIsNotAuth>
    );
  };

  return (
    <HeaderWrapper>
      <NavLink to={mainUrl} style={headerSignInStyles}>Realworld Blog</NavLink>
      {renderHeaderMenu()}
    </HeaderWrapper>
  );
};

Header.propTypes = {
  isAuthorized: propTypes.bool,
  username: propTypes.string,
  userProfileImage: propTypes.string,
  logOutSuccess: propTypes.func,
};

Header.defaultProps = {
  isAuthorized: false,
  username: null,
  userProfileImage: null,
  logOutSuccess: null,
};

export default connect(mapStateToProps, actionCreator)(Header);
