import React, { useEffect, useState } from 'react';
import { Pagination } from 'antd';
import { connect } from 'react-redux';
import * as actions from '../../redux/actions';
import { renderLoader } from '../../helper';
import {
  ArticlesWrapper,
  PaginationWrapper,
  ArticlesItemsWrapper,
} from '../../styled-components';
import Article from './article';
import { editArticleState } from '../../redux/reducers/stateReducers';

const mapStateToProps = (state) => {
  const { articles, getArticlesState, createArticleState, editArticleState } = state;

  const props = {
    articles,
    isArticlesReady: getArticlesState === 'finished',
    isRequestProcessed: getArticlesState === 'processed',
    isNewArticleCreated: createArticleState === 'finished',
    isArticleEdited: editArticleState === 'finished',
  };

  return props;
};

const actionCreator = {
  getArticles: actions.getArticles,
  refreshArticles: actions.refreshArticles,
};

const ArticlesList = (props) => {
  const {
    getArticles, articles, isArticlesReady, isRequestProcessed,
    isNewArticleCreated, refreshArticles, isArticleEdited
  } = props;

  const [currentPageNumber, setCurrentPageNumber] = useState(1);
  const [currentPageSize, setPageSize] = useState(5);

  // делаем фейковые лайки для визуального обновления без лишних запросов на сервер
  const [fakeLikes, setFakeLikes] = useState([]);

  const getArticlesRequest = async () => {
    await getArticles();
  };

  const renderArticles = (pageNumber, pageSize) => {
    const from = pageNumber * pageSize - pageSize;
    const to = pageNumber * pageSize;
    const currentArticlesArray = Array.from(articles).slice(from, to);
    const currentFakeLikes = Array.from(fakeLikes).slice(from, to);

    return <Article articles={currentArticlesArray} fakeLikes={currentFakeLikes} setFakeLikes={setFakeLikes} />;
  };

  const handleOnChangePagination = (value, pageSize) => {
    setCurrentPageNumber(value);
    setPageSize(pageSize);
  };

  const renderPagination = (total) => (
    <Pagination
      size="small"
      total={total}
      pageSize={5}
      showSizeChanger={false}
      onChange={handleOnChangePagination}
    />
  );

  useEffect(() => {
    // получаем посты пока они есть и убираем дублирование запроса если создан новый пост
    if (!isArticlesReady && !isNewArticleCreated) {
      getArticlesRequest();
    }

    // обновляем посты после создания нового
    if (isNewArticleCreated) refreshArticles();

    if (isArticleEdited) refreshArticles();
  }, [isArticlesReady, isNewArticleCreated]);

  useEffect(() => {
    // после каждой пачки постов делаем для них фейковые лайки
    setFakeLikes(() => articles.map((el) => ({
      id: el.id, isLiked: el.favorited, likesCount: el.favoritesCount, slug: el.slug,
    })));
  }, [articles]);

  return (
    <ArticlesWrapper>
      <ArticlesItemsWrapper>
        {(isRequestProcessed || isArticlesReady)
          ? renderArticles(currentPageNumber, currentPageSize)
          : renderLoader()}
      </ArticlesItemsWrapper>

      <PaginationWrapper>
        {renderPagination(articles.length)}
      </PaginationWrapper>
    </ArticlesWrapper>
  );
};

export default connect(mapStateToProps, actionCreator)(ArticlesList);
