import { combineReducers } from 'redux';
import { handleActions } from 'redux-actions';
import * as actions from '../actions';
import { removeItemFromLocalstorage, setItemToLocalstorage } from '../../localstorage';

const signUpState = handleActions({
  [actions.signUpRequest]() {
    return 'requested';
  },
  [actions.signUpFailure]() {
    return 'failed';
  },
  [actions.signUpSuccess]() {
    return 'finished';
  },
}, 'none');

const signInState = handleActions({
  [actions.signInRequest]() {
    return 'requested';
  },
  [actions.signInFailure]() {
    return 'failed';
  },
  [actions.signInSuccess]() {
    return 'finished';
  },
}, 'none');

const userDataInitial = { username: '', token: '', isAuthorized: false };
const userData = handleActions({
  [actions.signInSuccess](state, action) {
    const { username, token, isAuthorized, userProfileImage } = action.payload;
    setItemToLocalstorage('token', token);
    setItemToLocalstorage('username', username);
    return { username, token, isAuthorized, userProfileImage };
  },
  [actions.logOutSuccess]() {
    removeItemFromLocalstorage('token');
    removeItemFromLocalstorage('username');
    return userDataInitial;
  },
}, userDataInitial);

const getArticlesState = handleActions({
  [actions.getArticlesProcess]() {
    return 'processed';
  },
  [actions.getArticlesSuccess]() {
    return 'finished';
  },
}, 'none');

const createArticleState = handleActions({
  [actions.createArticleRequest]() {
    return 'requested';
  },
  [actions.createArticleFailure]() {
    return 'failed';
  },
  [actions.createArticleSuccess]() {
    return 'finished';
  },
}, 'none');

const articles = handleActions({
  [actions.getArticlesProcess](state, action) {
    return [...state, ...action.payload];
  },
  [actions.createArticleSuccess]() {
    return [];
  },
}, []);

export default combineReducers({
  signUpState,
  signInState,
  userData,
  getArticlesState,
  createArticleState,
  articles,
});
