import React from 'react';
import styled from 'styled-components';
import { Pagination } from 'antd';
import activeLikeSrc from '../../images/like-active.svg';
import disabledLikeSrc from '../../images/like-disabled.svg';
import userDefaultImgSrc from '../../images/user-default-img.svg';

const ArticlesWrapper = styled.div`
  background-color: #FFFFFF;
`;

const ArticlesItemsWrapper = styled.div`
  background: #E5E5E5;
`;

const ArticlesItem = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  background-color: #FFFFFF;
  padding: 16px;
`;

const ArticlesInfo = styled.div`
  display: grid;
  grid-column: span 3;
  grid-template-rows: auto auto auto;
  align-content: start;
  grid-gap: 8px;
`;

const ArticlesHeader = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
`;

const ArticlesTitle = styled.span`
  color: #1890FF;
  font-size: 20px;
`;

const ArticlesStats = styled.div`

`;

const ArtickesLikeImg = styled.img`
  width: 16px;
  height: 16px;
  margin: 0 0 0 8px;
  cursor: pointer;
`;


const ArticlesLikesCount = styled.span`
  margin: 0 0 0 4px;
`;

const ArticlesUserInfo = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  align-items: flex-start;
`;

const ArticlesTagsWrap = styled.div`

`;

const ArticlesTag = styled.span`
  border: 1px solid rgba(0, 0, 0, 0.5);
  border-radius: 2px;
  padding: 0 4px;

  &:not(:first-child) {
    margin: 0 0 0 8px;
  }
`;

const ArticlesDescription = styled.p``;

const ArticlesUserInfoGroup = styled.div`
  margin: 0px 16px 0px 0px;
  text-align: center;
`;

const ArticlesUsername = styled.p`
  margin: 0;
  font-size: 18px;
  font-family: Inter;
  font-style: normal;
  font-weight: normal;
  color: rgba(0, 0, 0, 0.85);
`;

const ArticlesDate = styled.p`
  color: rgba(0, 0, 0, 0.5);
  font-size: 12px;
  margin: 0;
`;

const ArticlesUserImage = styled.img`

`;

const PaginationWrapper = styled.div`
  display: flex;
  justify-content: center;
`;

const ArticlesList = () => {

  return (
    <ArticlesWrapper>
      <ArticlesItemsWrapper>
        <ArticlesItem>
          <ArticlesInfo>
            <ArticlesHeader>
              <ArticlesTitle>
                Some article title
              </ArticlesTitle>
              <ArticlesStats>
                <ArtickesLikeImg src={disabledLikeSrc}/>
                <ArticlesLikesCount>10</ArticlesLikesCount>
              </ArticlesStats>
            </ArticlesHeader>
            <ArticlesTagsWrap>
              <ArticlesTag>tag</ArticlesTag>
              <ArticlesTag>someTag</ArticlesTag>
            </ArticlesTagsWrap>

            <ArticlesDescription>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit,
              sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
              Ut enim ad minim veniam, quis nostrud exercitation ullamco
              laboris nisi ut aliquip ex ea commodo consequat
            </ArticlesDescription>
          </ArticlesInfo>

          <ArticlesUserInfo>
            <ArticlesUserInfoGroup>
              <ArticlesUsername>John Doe</ArticlesUsername>
              <ArticlesDate>March 5, 2020</ArticlesDate>
            </ArticlesUserInfoGroup>
            <ArticlesUserImage src={userDefaultImgSrc} />
          </ArticlesUserInfo>
        </ArticlesItem>

        <PaginationWrapper>
          <Pagination size="small" total={50} />
        </PaginationWrapper>
      </ArticlesItemsWrapper>


    </ArticlesWrapper>
  )
}

export default ArticlesList;
